import { useState, useEffect, useRef } from "react";
import Note from "./components/Note";
import noteService from "./services/notes";
import CreateNoteForm from "./components/CreateNoteForm";

function App() {
  const [notes, setNotes] = useState([]);
  const [message, setMessage] = useState(null)

  useEffect(() => {
    noteService.getAll().then((notes) => setNotes(notes));
  }, []);

  const addNote = (noteObject) => {
    noteService.create(noteObject).then((returnednote) => {
      setNotes(notes.concat(returnednote));
      setMessage(`a new note ${noteObject.note} added`);
      setTimeout(() => {
        setMessage(null);
      }, 5000);
    });
  };

  const handleRemove = (id) => {
    const noteToBeDeleted = notes.find((note) => note.id === id);
    if (window.confirm(`Remove note ${noteToBeDeleted.note}`)) {
      noteService.remove(id).then(() => {
        setNotes(notes.filter((note) => note.id !== id));
        setMessage(`Removed note ${noteToBeDeleted.note}`);
        setTimeout(() => {
          setMessage(null);
        }, 5000);
      });
    }
  };

  const sortedNotes = notes.sort(notes)

  return (
    <div>
      <h2>Notes</h2>
      <div className="basicText">
      </div>
      <button buttonLabel="New note" ref={CreateNoteForm}>
        <CreateNoteForm createNote={addNote} />
      </button>
      {sortedNotes.map((note) => (
        <Note
          key={note.id}
          note={note}
          handleRemove={handleRemove}
        />
      ))}
    </div>
  );
}

export default App;
