import { useState, useEffect, useRef } from "react";
import Note from "./components/Note";
import noteService from "./services/notes";
import CreateNoteForm from "./components/CreateNoteForm";



function App() {
  const [notes, setNotes] = useState([]);
  const [message, setMessage] = useState(null);
  const [editingNote, setEditingNote] = useState(null);
  const CreateNoteFormRef = useRef();

  useEffect(() => {
    noteService.getAll().then((notes) => setNotes(notes));
  }, []);

  const addNote = (noteObject) => {
    if (!editingNote) {
      // Adding a new note
      noteService.create(noteObject).then((returnedNote) => {
        setNotes(notes.concat(returnedNote));
        setMessage(`A new note "${noteObject.note}" added`);
        setTimeout(() => {
          setMessage(null);
        }, 5000);
      });
    } else {
      // Editing an existing note
      noteService.update(editingNote.id, noteObject).then((updatedNote) => {
        setNotes(notes.map((note) => (note.id === editingNote.id ? updatedNote : note)));
        setMessage(`Note "${noteObject.note}" updated`);
        setTimeout(() => {
          setMessage(null);
        }, 5000);
        setEditingNote(null);
      });
    }

    // Reset the form and editing state
    CreateNoteFormRef.current.resetForm();
    setEditingNote(null);
  };

  const handleEdit = (id) => {
    const noteToBeEdited = notes.find((note) => note.id === id);
    CreateNoteFormRef.current.setInitialValue(noteToBeEdited.note);
    setEditingNote(noteToBeEdited);
  };

  const handleRemove = (id) => {
    const noteToBeDeleted = notes.find((note) => note.id === id);
    if (window.confirm(`Remove note "${noteToBeDeleted.note}"`)) {
      noteService.remove(id).then(() => {
        setNotes(notes.filter((note) => note.id !== id));
        setMessage(`Removed note "${noteToBeDeleted.note}"`);
        setTimeout(() => {
          setMessage(null);
        }, 5000);
      });
    }
  };

  return (
    <div>
      <h2>Notes</h2>
      {message && <div className="message">{message}</div>}
      {notes.map((note) => (
        <Note
          key={note.id}
          note={note}
          handleRemove={handleRemove}
          handleEdit={() => handleEdit(note.id)}
        />
      ))}
      <CreateNoteForm createNote={addNote} ref={CreateNoteFormRef} />
    </div>
  );
}

export default App;
