import { useState, useEffect, useRef } from "react";
import Note from "./components/Note";
import noteService from "./services/notes";
import CreateNoteForm from "./components/CreateNoteForm";
import notesApi from "./services/notes";


function App() {
  const [notes, setNotes] = useState([]);
  const [message, setMessage] = useState(null);
  const [setEditingNote] = useState(null);
  const CreateNoteFormRef = useRef();
  const [newNote, setNewNote] = useState("");

  useEffect(() => {
    noteService.getAll().then((notes) => setNotes(notes));
  }, []);

const createNote = async (newNote) => {
  try {
    if (!newNote || typeof newNote !== "string") {
      // Handle the case where the new note is not a string
      console.error('Error: Invalid note format.');
      return;
    }

    // Convert to string and trim
    const trimmedNote = newNote.trim();

    if (trimmedNote === "") {
      // Handle the case where the trimmed note is empty
      console.error('Error: Empty note cannot be added.');
      return;
    }

    const createdNote = await notesApi.create({ note: trimmedNote });
    console.log('Note created:', createdNote);

    // Update the state with the new note
    setNotes((prevNotes) => [...prevNotes, createdNote]);
  } catch (error) {
    console.error('Error creating note:', error);
  }
};
  const handleEdit = (id) => {
    const noteToBeEdited = notes.find((note) => note.id === id);
    CreateNoteFormRef.current.setInitialValue(noteToBeEdited.note);
    setEditingNote(noteToBeEdited);
  };

  const handleRemove = (id) => {
    const noteToBeDeleted = notes.find((note) => note.id === id);
    if (window.confirm(`Remove note "${noteToBeDeleted.note}"`)) {
      noteService.remove(id).then(() => {
        setNotes(notes.filter((note) => note.id !== id));
        setMessage(`Removed note "${noteToBeDeleted.note}"`);
        setTimeout(() => {
          setMessage(null);
        }, 5000);
      });
    }
  };

  return (
    <div>
      <h2>Notes</h2>
      {message && <div className="message">{message}</div>}
      {notes.map((note) => (
        <Note
          key={note.id}
          note={note}
          handleRemove={handleRemove}
          handleEdit={() => handleEdit(note.id)}
        />
      ))}
      <CreateNoteForm createNote={createNote} />
    </div>
  );
}

export default App;
